module.exports = {
	throwUnlessEqual: function () {
		if (arguments[0] == arguments[1])
			return true
		else
			throw ('arguments not equal');
	},
	throwUnlessDefined: function () {
		if (typeof arguments[0] == "undefined")
			throw ('not defined');
	},
	throwNow: function () {
		throw ('defined');
	}
};